package io.firstcrudproject.firstcrude;

public class Calculator {
	
//Dosum
	public int doSum(int a, int b, int c) {
		// TODO Auto-generated method stub
		return a+b+c;
	}
	
	//Product
	public int Product(int a, int b) {
		return a*b;
	}
	
	//Compare
	public boolean Compare(int a, int b) {
		return a==b;
	}
}
